import { useState, KeyboardEvent } from "react"

export const Input: React.FC<{addTodo: (value: string, date: string) => void }> = (props) => {

	const [todo, setTodo] = useState<string>("")
	const [date, setDate] = useState<string>("")
	const [isHover, setIsHover] = useState<boolean>(false)

	const onEnter = (e: KeyboardEvent<HTMLInputElement>) => {
		if (e.key === 'Enter' && todo.trim() !== "") {
			//props.addTodo(todo)
			setTodo("")
		}
	}

	return (
		<div className="w-full h-2/3 flex items-end border-0 rounded-md bg-skin-main">
			<div className="w-1/12 h-full flex justify-center items-center">
				<div className="w-2/3 h-3/5 border border-skin-circle rounded-full flex justify-center items-center">
				</div>
			</div>
			<div className="h-full grow flex items center pl-5 border-r-2">
				<input type="text" placeholder="Create a new todo..." onChange={e => setTodo(e.target.value)} onKeyDown={onEnter} value={todo} className="w-full placeholder:font-['Josefin_Sans'] placeholder:text-[25px] placeholder:text-skin-color3 bg-skin-main outline-none border-0 rounded-md font-['Josefin_Sans'] text-[25px] text-skin-color4 caret-skin-caret" />
			</div>
			<div className="w-3/12 h-full flex justify-center items-center border-r-2">
				<input type="date" onChange={e => setDate(e.target.value)} value={date} className="bg-skin-main outline-none font-['Josefin_Sans'] text-[18px] text-skin-color4 caret-skin-caret"/>
			</div>
			<div className="w-1/12 h-full flex justify-center items-center">
				<button className="w-1/2 h-2/5 border-2 rounded-md flex justify-center items-center hover:bg-gradient-to-br hover:from-[#57DDFF] hover:to-[#C058F3] hover:border-none" onMouseEnter={() => setIsHover(true)} onMouseLeave={() => setIsHover(false)} onClick={() => props.addTodo(todo, date)} onKeyDown={onEnter}>
					{isHover ?	<img src="../icon-check.svg" alt="check" />
							:	<img src="../icon-cross.svg" alt="cross" width="50%" height="50%" />}
				</button>
			</div>
		</div>
	)
}
