import { useState } from "react"

export const FilterBar = () => {

	const [colorAll, setColorAll] = useState<string>("text-skin-color3")

	const onClickAll = () => {
		if (colorAll === "text-skin-color3")
			setColorAll("text-skin-filter")
		else
		setColorAll("text-skin-color3")
	}

	const style = `font-['Josefin_Sans'] text-[15px] ${colorAll} hover:text-skin-color4 font-medium`

	return (
		<>
		<div className="w-full h-[15%] grid grid-cols-3">
			<span className="justify-self-start self-center pl-5 font-['Josefin_Sans'] text-[18px] text-skin-color3">1 items left</span>
			<div className="justify-self-center self-center">
				<button onClick={onClickAll} className={style}>All</button>
				<button className="font-['Josefin_Sans'] text-[15px] text-skin-color3 hover:text-skin-color4 font-medium mx-5">Active</button>
				<button className="font-['Josefin_Sans'] text-[15px] text-skin-color3 hover:text-skin-color4 font-medium">Completed</button>
			</div>
		</div>
		</>
	)
}
