import React, { ReactNode, useCallback, useState } from 'react';
import { Background } from './comps/Background';
import { Bar } from './comps/Bar';
import { Input } from './comps/Input';
import { ListBox } from './comps/ListBox';
import { CompleteState } from './comps/ListElements/CompleteState';
import { HoverState } from './comps/ListElements/HoverState';
import { ListElement } from './comps/ListElements/ListElement';
import { NormalState } from './comps/ListElements/NormalState';
import { Wrapper } from './comps/Wrapper';

export interface ChildrenProps {
  children?: ReactNode
}

export interface ElementsProps {
  isHover: boolean
  onMouseEnter: () => void
  onMouseLeave: () => void
  isComplete: boolean
  value: string
}

export interface Todo {
  id: number
  value: string
  isCompleted: boolean
}

const App: React.FC = () => {

  const [isHover, setIsHover] = useState<boolean>(false)
  const [todos, setTodos] = useState<Todo[]>([])

  const addTodo = (value: string) => {
    setTodos(
      [...todos, {value: value, isCompleted: false}]
    )
  }

  const onMouseEnter = () => {
    setIsHover(true)
  }

  const onMouseLeave = () => {
    setIsHover(false)
  }

  console.log(todos)

  return (
    <div className='w-full h-full'>
    <Background>
      <Wrapper>
        <Bar/>
        <Input addTodo={addTodo} />
        <ListBox>
          {todos.map((todo) => {
            return (
              <ListElement isHover={isHover} onMouseEnter={onMouseEnter} onMouseLeave={onMouseLeave} isComplete={todo.isCompleted} value={todo.value} />
            )
          })}
        </ListBox>
      </Wrapper>
    </Background>
    </div>
  );
}

export default App;
